import { Link } from 'react-router-dom';
import '../css/Register.css';
import signUpImage from '../assets/undraw_fingerprint-login_19qv.png';

function Register() {
  const dispatch = useDispatch();
  const { loading, error, success } = useSelector((state) => state.register);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  useEffect(() => {
    if (success) {
      alert('Kayıt başarılı! Giriş yapabilirsiniz.');
      dispatch(clearRegisterState());
      // istersen yönlendirme yapabilirsin (örn. login sayfasına)
    }
  }, [success, dispatch]);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(registerUser(formData));
  };


    return (
        <div>
            <h2 className="registerTitle">Kayıt Sayfası</h2>

            <div className="RegisterMain">
                <div className="registerZone">
                    <label><span>İsim:</span><input /></label>
                    <label><span>E-mail:</span><input /></label>
                    <label><span>Şifre:</span><input /></label>
                    <button>Kaydol</button>
                    <p className='pHomePage'>Eğer kayıt olmadan anasayfaya gitmek istiyorsanız <Link to="/homepage">tıklayınız !</Link></p>
                </div>

                <div className="verticalDivider"></div>

                <div className="photographZone">
                    <h2>Bilgileriniz Token Teknojolisi ile Korunmaktadır.</h2>
                    <img src={signUpImage} alt="Kayıt Ol" className="registerImage" />
                </div>
            </div>
        </div>
    );
}

export default Register;
