import '../css/Login.css';
import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser, clearLoginState } from '../redux/slices/loginSlice';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  
  const { loading, error, user, success } = useSelector(state => state.login);
  
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  
  useEffect(() => {
    if(success && user) {
      navigate('/homepage'); 
      dispatch(clearLoginState());
    }
  }, [success, user, navigate, dispatch]);
  
  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(loginUser(formData));
  };
  
  return (
    <div className="formContainer">
      <h2 className="formTitle">Giriş Yap</h2>
<form onSubmit={handleSubmit} className="form">
  <div className="form-group">
    <label htmlFor="email">E-mail:</label>
    <input
      id="email"
      type="email"
      name="email"
      value={formData.email}
      onChange={handleChange}
      required
    />
  </div>
  <div className="form-group">
    <label htmlFor="password">Şifre:</label>
    <input
      id="password"
      type="password"
      name="password"
      value={formData.password}
      onChange={handleChange}
      required
    />
  </div>
  <button type="submit" disabled={loading}>
    {loading ? 'Giriş yapılıyor...' : 'Giriş Yap'}
  </button>
</form>

      {error && <p className="error-message">{error}</p>}
      <p>
        Kayıtlı değil misiniz? <Link to="/register">Kayıt olun!</Link>
      </p>
    </div>
  );
}

export default Login;
