import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { registerUser, clearRegisterState } from '../redux/slices/registerSlice';
import '../css/Register.css';
import signUpImage from '../assets/undraw_fingerprint-login_19qv.png';
import backgroundVideoRegister from '../assets/13523849_2160_3840_100fps.mp4';

function Register() {
  const dispatch = useDispatch();
  const { loading, error, success } = useSelector((state) => state.register);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  useEffect(() => {
    if (success) {
      alert('Kayıt başarılı! Giriş yapabilirsiniz.');
      dispatch(clearRegisterState());
    }
  }, [success, dispatch]);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(registerUser(formData));
  };

  return (
    <div>
      <video autoPlay loop muted className="backgroundVideoRegister">
        <source src={backgroundVideoRegister} type="video/mp4" />
          Tarayıcınız video etiketini desteklemiyor.
      </video>
      <h2 className="registerTitle">Kayıt Sayfası</h2>
      <div className="RegisterMain">
        <form className="registerZone" onSubmit={handleSubmit}>
          <label>
            <span>İsim:</span>
            <input
              name="name"
              type="text"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            <span>E-mail:</span>
            <input
              name="email"
              type="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            <span>Şifre:</span>
            <input
              name="password"
              type="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </label>
          <button type="submit" disabled={loading}>
            {loading ? 'Kaydoluyor...' : 'Kaydol'}
          </button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <p>
            Eğer kayıt olmadan anasayfaya gitmek istiyorsanız{' '}
            <Link to="/homepage">tıklayınız !</Link>
          </p>
        </form>

        <div className="verticalDivider"></div>

        <div className="photographZone">
          <h2>Tüm bilgileriniz Token ile gizlenmektedir.</h2>
          <img
            src={signUpImage}
            alt="Kayıt Ol"
            className="registerImage"
          />
        </div>
      </div>
    </div>
  );
}

export default Register;
